#

def getPower(num, pwr):
    return num**pwr

def getPowerSum(numPwr):
    total = 0
    for digit in str(numPwr):
        total = total + int(digit)
    return total

number = 2
power = 1000
exp = getPower(number, power)
print(f"The number {number} to the power of {power} is {str(exp)}. \n\nThe sum of all those digits is {str(getPowerSum(exp))}")